    const tx = await contract.sellTicketBack(tokenId);
    const receipt = await tx.wait();
    return (_receipt$transactionH2 = receipt === null || receipt === void 0 ? void 0 : receipt.transactionHash) !== null && _receipt$transactionH2 !== void 0 ? _receipt$transactionH2 : null;
  } catch (error) {
