    throw new Error(error.message);
  }
};

// Corrected getEventDetails (uses EventManager)
export const getEventDetails = async () => {
  const contract = await getEventManagerContract(true);
  if (!contract) return null;
  try {
    const [active, maxTickets, currentTickets, ticketPriceInsider, ticketPriceOutsider] = await Promise.all([contract.eventActive(), contract.maxTickets(), contract.ticketCount(), contract.ticketPriceInsider(), contract.ticketPriceOutsider()]);
    return {
      isActive: active,
      maxTickets: Number(maxTickets),
      currentTickets: Number(currentTickets),
      name: "BlockFest Event",
      // Default name since contract doesn't have this
      description: "BlockFest Event Description",
      // Default description since contract doesn't have this
      startDate: "TBD",
      // Default since contract doesn't have this
      endDate: "TBD",
      // Default since contract doesn't have this
      ticketPrice: ticketPriceInsider,
      // Using insider price as default
      ticketPriceInsider: ticketPriceInsider,
      ticketPriceOutsider: ticketPriceOutsider
    };
  } catch (error) {
    console.error("Error getting event details:", error);
    return null;
  }
};

// Corrected getTokenMetadataURI (uses TicketNFT)
export const getTokenMetadataURI = async tokenId => {
  const contract = await getTicketNFTContract(true);
  if (!contract) return null;
  try {
    const uri = await contract.tokenURI(tokenId);
    return uri;
  } catch (error) {
    console.error(`Error getting token URI for ${tokenId}:`, error);
    return null;
  }
};

// --- Updated getAvailableTickets (fetches ALL issued IDs) ---
export const getAvailableTickets = async () => {
  const contract = await getEventManagerContract(true);
  if (!contract) return [];
  try {
    const countBN = await contract.ticketCount();
    const count = Number(countBN);
    if (count === 0) return [];
    console.log(`Total tickets issued: ${count}. Fetching IDs...`);
    const ids = [];
    const promises = [];
    for (let i = 0; i < count; i++) {
      promises.push(contract.issuedTickets(i));
    }
    const results = await Promise.all(promises);
    results.forEach(tokenIdBN => {
      ids.push(Number(tokenIdBN));
    });
    console.log("All issued ticket IDs fetched:", ids);
    return ids;
  } catch (error) {
    console.error("Error getting available/issued tickets:", error);
    toast.error("Could not retrieve list of issued tickets.");
    return [];
  }
};

// Corrected getQRHash (uses TicketNFT)
export const getQRHash = async tokenId => {
  const nftContract = await getTicketNFTContract(true);
  if (!nftContract) return null;
  try {
    // Ensure TICKET_NFT_ABI includes getQRHash
    const qrHash = await nftContract.getQRHash(tokenId);
    return qrHash;
  } catch (error) {
    console.error(`Error getting QR Hash for token ${tokenId}:`, error.message);
    return null;
  }
};

