// Basic validation
if (!CONTRACT_ADDRESS) {
    throw new Error('REACT_APP_CONTRACT_ADDRESS environment variable is required');
}
if (!TARGET_NETWORK_ID) console.error("REACT_APP_TARGET_NETWORK_ID not set.");
if (EVENT_MANAGER_ABI.length === 0) console.error("EVENT_MANAGER_ABI is empty.");
if (TICKET_NFT_ABI.length === 0) console.error("TICKET_NFT_ABI is empty.");

interface NetworkInfo { chainId: number; name: string; }

// --- Helper Functions ---
const getProvider = (): ethers.BrowserProvider | null => {
    if (typeof window.ethereum === 'undefined') {
        console.error('MetaMask not detected');
        return null;
    }
    return new ethers.BrowserProvider(window.ethereum);
};

const getSigner = async (provider: ethers.BrowserProvider): Promise<ethers.Signer | null> => {
    try {
        const accounts = await provider.send('eth_requestAccounts', []);
        if (accounts.length === 0) return null;
        return provider.getSigner(accounts[0]);
    } catch (error) {
        console.error('Failed to get signer:', error);
        return null;
    }
};

const getEventManagerContract = async (readOnly = false): Promise<ethers.Contract | null> => {
    const provider = getProvider();
    if (!provider) return null;

    const signer = readOnly ? provider : await getSigner(provider);
    if (!signer) return null;

    return new ethers.Contract(CONTRACT_ADDRESS, EVENT_MANAGER_ABI, signer);
};

const getTicketNFTContract = async (readOnly = false): Promise<ethers.Contract | null> => {
    const provider = getProvider();
    if (!provider) return null;

    const signer = readOnly ? provider : await getSigner(provider);
    if (!signer) return null;

    const eventManager = await getEventManagerContract(true);
    if (!eventManager) return null;

    try {
        const ticketNFTAddress = await eventManager.ticketNFT();
        return new ethers.Contract(ticketNFTAddress, TICKET_NFT_ABI, signer);
    } catch (error) {
        console.error('Failed to get TicketNFT contract:', error);
        return null;
    }
};

// --- Core Blockchain Functions ---
export const connectWallet = async (): Promise<string | null> => {
    const provider = getProvider();
    if (!provider) return null;

    const signer = await getSigner(provider);
    if (!signer) return null;

    const address = await signer.getAddress();
    return address;
};
export const checkNetwork = async (): Promise<{ isCorrect: boolean; currentNetwork: NetworkInfo | null }> => {
    const provider = getProvider();
    if (!provider) return { isCorrect: false, currentNetwork: null };

    try {
        const network = await provider.getNetwork();
        const currentNetwork: NetworkInfo = {
            chainId: Number(network.chainId),
            name: network.name
        };

        return {
            isCorrect: Number(network.chainId) === TARGET_NETWORK_ID,
            currentNetwork
        };
    } catch (error) {
        console.error('Error checking network:', error);
        return { isCorrect: false, currentNetwork: null };
    }
};
export const switchNetwork = async (): Promise<boolean> => {
    if (typeof window.ethereum === 'undefined') {
        console.error('MetaMask not detected');
        return false;
    }

    try {
        await window.ethereum.request({
            method: 'wallet_switchEthereumChain',
            params: [{ chainId: ethers.hexlify(TARGET_NETWORK_ID.toString()) }]
        });
        return true;
    } catch (error: any) {
        if (error.code === 4902) {
            try {
                await window.ethereum.request({
                    method: 'wallet_addEthereumChain',
                    params: [{
                        chainId: ethers.hexlify(TARGET_NETWORK_ID.toString()),
                        chainName: TARGET_NETWORK_NAME,
                        nativeCurrency: {
                            name: 'ETH',
                            symbol: 'ETH',
                            decimals: 18
                        },
                        rpcUrls: [process.env.REACT_APP_RPC_URL || '']
                    }]
                });
                return true;
            } catch (addError) {
                console.error('Error adding network:', addError);
                return false;
            }
        }
        console.error('Error switching network:', error);
        return false;
    }
};

// Corrected buyTicket
export const buyTicket = async (
    tokenURI: string,
    qrHash: string,
    outsider: boolean,
    priceInEther: string
): Promise<string | null> => {
    const contract = await getEventManagerContract(false); // EventManager handles buying
    if (!contract) throw new Error("EventManager Contract not available.");
    if (!priceInEther) throw new Error("Price is required to buy ticket.");

    try {
        const value = parseEther(priceInEther);
        console.log(`Calling contract.buyTicket with URI: ${tokenURI}, QR: ${qrHash}, Outsider: ${outsider}, Value: ${value.toString()}`);
        const tx = await contract.buyTicket(tokenURI, qrHash, outsider, { value });
        const receipt = await tx.wait();
        console.log(`Transaction Confirmed: ${receipt?.transactionHash}`);
        return receipt?.transactionHash ?? null;
    } catch (error: any) {
        console.error("Buy Ticket Error:", error);
        const reason = error.reason || error?.data?.message || error.message || 'Unknown error';
        if (error.code === 4001) throw new Error('Transaction rejected.');
        throw new Error(`Purchase Failed: ${reason}`);
    }
};

// --- *** CORRECTED getMyTickets using Event Querying *** ---
export const getMyTickets = async (userAddress: string): Promise<number[]> => {
    if (!userAddress) return [];

    const provider = getProvider();
    const nftContract = await getTicketNFTContract(true); // Get read-only NFT contract

    if (!provider || !nftContract) {
        console.error("getMyTickets: Could not get Provider or TicketNFT contract instance.");
        throw new Error("Failed to connect to NFT contract.");
    }

    try {
        console.log(`Querying Transfer events for recipient ${userAddress} on contract ${await nftContract.getAddress()}`);

        // Define the Transfer event filter - transfers TO the userAddress
        const filterTo = nftContract.filters.Transfer(null, userAddress);
        // Define the Transfer event filter - transfers FROM the userAddress
        const filterFrom = nftContract.filters.Transfer(userAddress);

        // Query events (adjust block range as needed for performance - 'earliest' can be slow)
        // Consider storing the last queried block number locally and starting from there.
        const transferEventsTo = await nftContract.queryFilter(filterTo, 'earliest', 'latest');
        const transferEventsFrom = await nftContract.queryFilter(filterFrom, 'earliest', 'latest');

        console.log(`Found ${transferEventsTo.length} Transfer(to) events`);
        console.log(`Found ${transferEventsFrom.length} Transfer(from) events`);

        // Process events to determine current ownership
        const ownedTokenIds = new Map<number, boolean>();

        // Add tokens transferred TO the user
        transferEventsTo.forEach((event: any) => {
            if (event.args && event.args.tokenId) {
                ownedTokenIds.set(Number(event.args.tokenId), true); // Mark as potentially owned
            }
        });

        // Remove tokens transferred FROM the user
        transferEventsFrom.forEach((event: any) => {
            if (event.args && event.args.tokenId) {
                ownedTokenIds.delete(Number(event.args.tokenId)); // Remove if transferred away
            }
        });

        const finalOwnedIds = Array.from(ownedTokenIds.keys());
        console.log("Final calculated owned ticket IDs:", finalOwnedIds);
        return finalOwnedIds;

    } catch (error: any) {
        console.error("Error querying Transfer events in getMyTickets:", error);
        throw new Error("Failed to fetch owned tickets via event query.");
    }
};

// Corrected resellTicket (uses EventManager)
export const resellTicket = async (tokenId: number): Promise<string | null> => {
    const contract = await getEventManagerContract(false);
    if (!contract) throw new Error("EventManager Contract not available.");

    try {
        const tx = await contract.sellTicketBack(tokenId);
        const receipt = await tx.wait();
        return receipt?.transactionHash ?? null;
    } catch (error: any) {
        console.error("Error reselling ticket:", error);
        throw new Error(error.message);
    }
};

// Corrected getEventDetails (uses EventManager)
export const getEventDetails = async (): Promise<EventDetails | null> => {
    const contract = await getEventManagerContract(true);
    if (!contract) return null;

    try {
        const [active, maxTickets, currentTickets, ticketPriceInsider, ticketPriceOutsider] = await Promise.all([
            contract.eventActive(),
            contract.maxTickets(),
            contract.ticketCount(),
            contract.ticketPriceInsider(),
            contract.ticketPriceOutsider()
        ]);

        return {
            isActive: active,
            maxTickets: Number(maxTickets),
            currentTickets: Number(currentTickets),
            name: "BlockFest Event", // Default name since contract doesn't have this
            description: "BlockFest Event Description", // Default description since contract doesn't have this
            startDate: "TBD", // Default since contract doesn't have this
            endDate: "TBD", // Default since contract doesn't have this
            ticketPrice: ticketPriceInsider, // Using insider price as default
            ticketPriceInsider: ticketPriceInsider,
            ticketPriceOutsider: ticketPriceOutsider
        };
    } catch (error) {
        console.error("Error getting event details:", error);
        return null;
    }
};

