// src/pages/MarketplacePage.tsx

import React, { useState, useEffect, useCallback } from 'react';
import { toast } from 'react-hot-toast';
import { motion } from 'framer-motion';
// Import specific utilities from ethers
import { formatEther, parseEther } from 'ethers'; // Keep parseEther for potential future use

// --- Context Hooks ---
import { useAuth, useWallet } from '../contexts/AuthContext';
import { useVIP } from '../contexts/VIPContext';

// --- Blockchain Utilities ---
import {
    buyTicket as buyTicketUtil,
    getMyTickets as getMyTicketsUtil,
    resellTicket as resellTicketUtil,
    getEventDetails,
    getTokenMetadataURI,
    getAvailableTickets as getAvailableTicketsUtil // Assuming this function exists
} from '../utils/blockchain';
import fetchMetadataFromUri from '../utils/metadata';

// --- Components ---
import GlowingButton from '../components/GlowingButton';
import VIPVerificationForm from '../components/VIPVerificationForm';
import NFTTicketCard from '../components/NFTTicketCard';
import NFTTicketCardSkeleton from '../components/NFTTicketCardSkeleton';

// --- Type Definitions ---
interface TicketData {
    id: number;
    eventName: string;
    price: string; // Price stored as formatted string (e.g., "0.05")
    seatNumber: string;
    qrValue: string;
    imageUrl?: string;
    metadata?: any; // Added to use fetchMetadataFromUri later
}

// --- Mock Data --- REMOVE LATER ---
const MOCK_AVAILABLE_TICKETS: Omit<TicketData, 'price'>[] = [
    {
        id: 1,
        eventName: 'Genesis Block Party',
        seatNumber: 'GA-101',
        qrValue: 'blockfest-ticket-1',
        imageUrl: 'https://via.placeholder.com/300x200/6f42c1/ffffff?text=Genesis+Block+Party'
    },
    {
        id: 3,
        eventName: 'Crypto Carnival',
        seatNumber: 'SEC-A-55',
        qrValue: 'blockfest-ticket-3',
        imageUrl: 'https://via.placeholder.com/300x200/1b1b32/ffffff?text=Crypto+Carnival'
    },
];

// Flag to control whether to use mock data or real contract data
const USE_MOCK_DATA = true; // Set to false when ready to use real data
// --------------------------------------


const MarketplacePage: React.FC = () => {
    const { walletAddress, networkState, switchNetwork } = useWallet();
    const { user } = useAuth();
    const { isVIP } = useVIP(); // Removed unused checkVIPStatus, resetVIP for now
    const [availableTickets, setAvailableTickets] = useState<TicketData[]>([]);
    const [ownedTickets, setOwnedTickets] = useState<TicketData[]>([]);
    const [loadingTickets, setLoadingTickets] = useState<boolean>(true); // Start true initially
    const [buyingTicketId, setBuyingTicketId] = useState<string | number | null>(null);
    const [resellingTicketId, setResellingTicketId] = useState<number | null>(null);
    const [eventPrice, setEventPrice] = useState<string | null>(null); // Store event price as formatted ETHER string
    const [refreshTrigger, setRefreshTrigger] = useState<number>(0); // Add refresh trigger state

    // --- Effects ---
    useEffect(() => {
        const fetchPrice = async () => {
            try {
                const details = await getEventDetails();
                // --- FIX: Use the correct field name from your contract/details object ---
                // Using 'ticketPriceInsider' based on previous error message, adjust if needed
                const priceBigNumber = details?.ticketPriceInsider; // Example field

                if (priceBigNumber) {
                    // Use imported formatEther directly
                    setEventPrice(formatEther(priceBigNumber));
                } else {
                    console.warn("Ticket price (e.g., ticketPriceInsider) not found in event details.");
                    toast.error("Could not determine event price.");
                }
            } catch (error) {
                console.error("Failed to fetch event details:", error);
                toast.error("Could not load event details.");
            }
        };
        fetchPrice();
    }, []); // Run once on mount

    // --- Data Fetching Functions --- (Using useCallback to fix dependency warning)
    const fetchOwnedTickets = useCallback(async () => {
        if (!walletAddress) return;
        console.log("Fetching owned tickets...");
        try {
            const ownedIds = await getMyTicketsUtil(walletAddress);
            // Fetch details including metadata for each owned ID
            const ownedDetailsPromises = ownedIds.map(async (id: number): Promise<TicketData | null> => {
                try {
                    // 1. Get Token URI from contract
                    const uri = await getTokenMetadataURI(id); // Use blockchain util
                    // 2. Fetch Metadata from URI
                    const metadata = await fetchMetadataFromUri(uri); // Use helper
                    // 3. Get other details if needed (e.g., specific seat from contract?)
                    // const qrHash = await getQRHash(id); // Example
                    // 4. Construct TicketData object
                    return {
                        id,
                        // Use data from metadata if available, fallback otherwise
                        eventName: metadata?.name || `Owned Ticket ${id}`,
                        price: eventPrice || 'N/A', // Price usually from event, not metadata
                        // Seat number might be an attribute in metadata or stored separately
                        seatNumber: metadata?.attributes?.find((attr: any) => attr.trait_type === 'Seat')?.value || `Seat-${id}`,
                        qrValue: `qr-${id}`, // Replace with actual QR data source if available
                        imageUrl: metadata?.image || undefined, // Get image URL from metadata
                        metadata // Store full metadata for potential future use
                    };
                } catch (detailError) {
                    console.error(`Failed to fetch details for owned token ${id}:`, detailError);
                    return null;
                }
            });
            const resolvedDetails = (await Promise.all(ownedDetailsPromises)).filter(Boolean) as TicketData[];
            setOwnedTickets(resolvedDetails);
        } catch (error) {
            console.error("Failed to fetch owned tickets:", error);
            toast.error("Could not load your owned tickets.");
            setOwnedTickets([]);
        }
    }, [walletAddress, eventPrice]);

    const fetchAvailableTickets = useCallback(async () => {
        if (!walletAddress || !eventPrice) return;
        console.log("Fetching available tickets...");
        try {
            if (USE_MOCK_DATA) {
                // Calculate ETH price in wei for potential future operations (using parseEther here)
                const priceInWei = parseEther(eventPrice);
                console.log("Price in Wei:", priceInWei.toString());

                // Using mock data with short delay to simulate API call
                await new Promise(resolve => setTimeout(resolve, 500));
                
                // Filter out tickets the user already owns
                const currentOwnedIds = ownedTickets.map(t => t.id);
                const mockAvailable = MOCK_AVAILABLE_TICKETS
                    .filter(t => !currentOwnedIds.includes(t.id))
                    .map(t => ({ ...t, price: eventPrice }) as TicketData);
                
                setAvailableTickets(mockAvailable);
            } else {
                // Real implementation using blockchain
                // 1. Get available ticket IDs
                const availableIds = await getAvailableTicketsUtil();
                
                // 2. Fetch metadata for each available ticket
                const availableDetailsPromises = availableIds.map(async (id: number): Promise<TicketData | null> => {
                    try {
                        // Get Token URI from contract
                        const uri = await getTokenMetadataURI(id);
                        // Fetch Metadata from URI
                        const metadata = await fetchMetadataFromUri(uri);
                        
                        // Construct TicketData object
                        return {
                            id,
                            // Use data from metadata if available, fallback otherwise
                            eventName: metadata?.name || `Ticket ${id}`,
                            price: eventPrice,
                            // Seat number from metadata attribute
                            seatNumber: metadata?.attributes?.find((attr: any) => attr.trait_type === 'Seat')?.value || `Seat-${id}`,
                            qrValue: `qr-${id}`, // Placeholder for QR value
                            imageUrl: metadata?.image || undefined, // Image URL from metadata
                            metadata // Store full metadata
                        };
                    } catch (detailError) {
                        console.error(`Failed to fetch details for available token ${id}:`, detailError);
                        return null;
                    }
                });
                
                const resolvedDetails = (await Promise.all(availableDetailsPromises)).filter(Boolean) as TicketData[];
                setAvailableTickets(resolvedDetails);
            }
        } catch (error) {
            console.error("Failed to fetch available tickets:", error);
            toast.error("Could not load available tickets.");
            setAvailableTickets([]);
        }
    }, [walletAddress, eventPrice, ownedTickets]);

    // Function to load all ticket data
    const loadAllTicketData = useCallback(async () => {
        // Reset loading state
        setLoadingTickets(true);

        try {
            if (walletAddress && user && networkState?.isCorrect && eventPrice !== null) {
                // First fetch owned tickets
                await fetchOwnedTickets();
                
                // Then fetch available tickets if the user is VIP or VIP not required
                const vipRequired = process.env.REACT_APP_VIP_REQUIRED === 'true';
                if (!vipRequired || isVIP === true) {
                    await fetchAvailableTickets();
                } else if (isVIP === false) {
                    setAvailableTickets([]);
                    console.log("VIP required, user is not VIP. Not fetching available tickets.");
                }
            } else {
                // Clear data if prerequisites not met
                setAvailableTickets([]);
                setOwnedTickets([]);
                
                // Log helpful debug info
                if (!walletAddress) console.log("Waiting for wallet connection...");
                if (!user) console.log("Waiting for user login...");
                if (!networkState?.isCorrect) console.log("Waiting for correct network...");
                if (!eventPrice) console.log("Waiting for event price...");
            }
        } catch (error) {
            console.error("Error loading ticket data:", error);
            toast.error("Failed to load ticket information.");
        } finally {
            // Always set loading to false when done
            setLoadingTickets(false);
        }
    }, [walletAddress, user, networkState?.isCorrect, eventPrice, isVIP, fetchOwnedTickets, fetchAvailableTickets]);

    // Effect to load data when dependencies change or refresh is triggered
    useEffect(() => {
        console.log("Refresh triggered, loading all ticket data...");
        loadAllTicketData();
    }, [ refreshTrigger]); // Add refreshTrigger to dependencies

    // --- Event Handlers ---
    const handleBuyTicket = async (ticketId: string | number) => {
        // 1. Pre-checks
        if (!walletAddress || !user) return toast.error('Connect wallet and log in first.');
        if (!networkState?.isCorrect) { toast.error('Incorrect network. Please switch.'); await switchNetwork(); return; }
        if (isVIP === null && process.env.REACT_APP_VIP_REQUIRED === 'true') return toast.error('Please complete VIP verification.');
        if (!eventPrice) return toast.error('Event price is not available.'); // Crucial check

        // 2. VIP Limit Check
        const isInsider = isVIP === true;
        const ownedTicketCount = ownedTickets.length;
        if (isInsider && ownedTicketCount >= 1 && process.env.REACT_APP_VIP_LIMIT === 'true') {
            return toast.error("VIP members are limited to 1 ticket.");
        }

        // 3. Execute Buy
        setBuyingTicketId(ticketId);
        const toastId = `buy-${ticketId}`;
        toast.loading('Processing transaction...', { id: toastId });
        
        try {
            const ticketIdNum = Number(ticketId);
            const quantity = 1;
            const isOutsider = !isInsider;

            // Call the buy ticket utility with proper parameters
            const txHash = await buyTicketUtil(
                String(ticketIdNum), // Convert to string as required by buyTicketUtil
                eventPrice,
                Boolean(quantity),
                isOutsider.toString()
            );

            // Show success message with transaction link
            toast.success(
                <>
                    Ticket Purchased! <a href={`https://sepolia.etherscan.io/tx/${txHash}`} target="_blank" rel="noopener noreferrer" className='underline'>View Tx</a>
                </>, 
                { id: toastId, duration: 6000 }
            );
            
            // Refresh ticket data
            setRefreshTrigger(prev => prev + 1);
            console.log("2");
        } catch (error: any) {
            console.error("Buy Ticket Failed:", error);
            toast.error(`Purchase Failed: ${error.message}`, { id: toastId, duration: 6000 });
        } finally {
            setBuyingTicketId(null);
        }
    };

    const handleResaleTicket = async (tokenId: number) => {
        if (!walletAddress || !user) return toast.error('Connect wallet and log in first.');
        if (!networkState?.isCorrect) { toast.error('Incorrect network. Please switch.'); await switchNetwork(); return; }

        setResellingTicketId(tokenId);
        const toastId = `resell-${tokenId}`;
        toast.loading('Processing resale...', { id: toastId });
        
        try {
            const txHash = await resellTicketUtil(tokenId);
            
            toast.success(
                <>
                    Resale Submitted! <a href={`https://sepolia.etherscan.io/tx/${txHash}`} target="_blank" rel="noopener noreferrer" className='underline'>View Tx</a>
                </>, 
                { id: toastId, duration: 6000 }
            );
            
            // Refresh ticket data
            setRefreshTrigger(prev => prev + 1);
            console.log("3")
        } catch (error: any) {
            console.error("Resell Failed:", error);
            toast.error(`Resale Failed: ${error.message}`, { id: toastId, duration: 6000 });
        } finally {
            setResellingTicketId(null);
        }
    };

    // --- VIP Verification Handler ---
    const handleVIPVerified = useCallback(() => {
        // Force a refresh of the marketplace data after successful VIP verification
        console.log("VIP verified, refreshing marketplace data...");
        setRefreshTrigger(prev => prev + 1);
        console.log("4")
    }, []);

    // --- Render Logic ---
    // Basic checks before rendering main content
    if (!walletAddress) return (
        <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} className="flex items-center justify-center min-h-[400px]">
            <p className="text-center text-accent-light text-lg animate-fade-in">Please connect your wallet to access the marketplace.</p>
        </motion.div>
    );
    
    if (!user) return (
        <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} className="flex items-center justify-center min-h-[400px]">
            <p className="text-center text-accent-light text-lg animate-fade-in">Please log in to view the marketplace.</p>
        </motion.div>
    );
    
    if (!networkState?.isCorrect) return (
        <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} className="flex flex-col items-center justify-center min-h-[400px] space-y-4">
            <p className="text-center text-error text-lg animate-fade-in">
                Wrong Network Detected ({networkState?.currentNetwork?.name || 'Unknown'}).
            </p>
            <GlowingButton onClick={switchNetwork}>Switch Network</GlowingButton>
        </motion.div>
    );

    // VIP Check Step with updated callback
    if (process.env.REACT_APP_VIP_REQUIRED === 'true' && isVIP !== true) {
        // If VIP required and not VIP, show verification form
        return (
            <motion.div 
                initial={{ opacity: 0 }} 
                animate={{ opacity: 1 }} 
                className="max-w-md mx-auto my-8"
            >
                <VIPVerificationForm onVerified={handleVIPVerified} />
            </motion.div>
        );
    }

    // Main Marketplace View
    return (
        <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} className="space-y-12">
            {/* My Tickets Section */}
            <section>
                <h2 className="text-2xl font-bold text-accent-light mb-6">My Tickets</h2>
                {loadingTickets ? (
                    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
                        {[...Array(2)].map((_, index) => <NFTTicketCardSkeleton key={`owned-skeleton-${index}`} />)}
                    </div>
                ) : ownedTickets.length > 0 ? (
                    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
                        {ownedTickets.map((ticket) => (
                            <NFTTicketCard
                                key={`owned-${ticket.id}`}
                                ticket={ticket}
                                onBuy={() => {}} // Owned tickets don't have a buy button
                                isOwned={true}
                                onResell={handleResaleTicket}
                                isReselling={resellingTicketId === ticket.id}
                            />
                        ))}
                    </div>
                ) : (
                    <div className="bg-secondary-dark p-6 rounded-lg text-center">
                        <p className="text-text-secondary">You don't own any tickets yet.</p>
                    </div>
                )}
            </section>

            {/* Available Tickets Section */}
            <section>
                <h2 className="text-2xl font-bold text-accent-light mb-6">Available Tickets</h2>
                {loadingTickets ? (
                    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
                        {[...Array(4)].map((_, index) => <NFTTicketCardSkeleton key={`available-skeleton-${index}`} />)}
                    </div>
                ) : availableTickets.length > 0 ? (
                    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
                        {availableTickets.map((ticket) => (
                            <NFTTicketCard
                                key={`available-${ticket.id}`}
                                ticket={ticket}
                                onBuy={handleBuyTicket}
                                isOwned={false}
                                isBuying={buyingTicketId === ticket.id}
                            />
                        ))}
                    </div>
                ) : (
                    <div className="text-center text-text-secondary bg-secondary-dark p-6 rounded-lg">
                        <p>No tickets currently available for purchase.</p>
                    </div>
                )}
            </section>
        </motion.div>
    );
};

export default MarketplacePage;
