// src/App.tsx

// --- ALL IMPORTS AT THE TOP ---
import React, { useState, createContext, useContext, ReactNode, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, useLocation } from 'react-router-dom';
import { Toaster, toast } from 'react-hot-toast';
import { User } from 'firebase/auth';

// Import Pages and Components
import LandingPage from './pages/LandingPage';
import MarketplacePage from './pages/MarketplacePage';
import AdminDashboard from './pages/AdminDashboard';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import './index.css';

// Import the AuthProvider and useAuth from contexts/AuthContext
import { AuthProvider, useAuth } from './contexts/AuthContext';

// Import API functions
import { checkVIPApi } from './firebase/index';

// Import Network utilities
import {
    checkNetwork,
    switchNetwork as switchNetworkUtil
} from './utils/blockchain';

// --- VIP Context Definition ---
interface VIPState {
    isVIP: boolean | null;
    checkingVIP: boolean;
    checkVIPStatus: (name: string, rollNumber: string) => Promise<boolean>;
    resetVIP: () => void;
}
const VIPContext = createContext<VIPState | undefined>(undefined);

// --- Network State Types ---
interface NetworkState { 
    isCorrect: boolean; 
    currentNetwork: { 
        chainId: number; 
        name: string 
    } | null;
}

// --- VIP Provider Implementation ---
const VIPProvider = ({ children }: { children: ReactNode }) => {
    const [isVIP, setIsVIP] = useState<boolean | null>(null);
    const [checkingVIP, setCheckingVIP] = useState<boolean>(false);
    const auth = useAuth(); // Using the imported useAuth hook

    useEffect(() => {
        // Reset VIP if user logs out
        if (!auth.user) {
            setIsVIP(null);
        }
    }, [auth.user]);

    const checkVIPStatus = async (name: string, rollNumber: string): Promise<boolean> => {
        if (!auth.user) {
            toast.error("Please log in before checking VIP status.");
            return false;
        }
        setCheckingVIP(true);
        toast.loading('Verifying VIP Status...', { id: 'vip-check' });
        try {
            const response = await checkVIPApi(name, rollNumber);
            setIsVIP(response.isVIP);
            toast.dismiss('vip-check');
            if (response.isVIP) {
                toast.success('✅ You are a VIP!');
            } else {
                toast.error('❌ Access Denied. Not on VIP list.');
            }
            setCheckingVIP(false);
            return response.isVIP;
        } catch (error: any) {
            console.error("VIP Check API Error:", error);
            toast.dismiss('vip-check');
            toast.error(error.message || 'Error checking VIP status.');
            setIsVIP(false);
            setCheckingVIP(false);
            return false;
        }
    };

    const resetVIP = () => {
        setIsVIP(null);
    };

    const value = { isVIP, checkingVIP, checkVIPStatus, resetVIP };

    return (
        <VIPContext.Provider value={value}>{children}</VIPContext.Provider>
    );
};

// --- useVIP Hook ---
export const useVIP = () => {
    const context = useContext(VIPContext);
    if (!context) throw new Error('useVIP must be used within a VIPProvider');
    return context;
};

// --- Main App Component ---
function App() {
    return (
        <AuthProvider>
            <Router>
                <VIPProvider>
                    <AppContent />
                </VIPProvider>
            </Router>
        </AuthProvider>
    );
}

// --- AppContent Component with Router-dependent logic ---
function AppContent() {
    const { user, loading } = useAuth();
    const location = useLocation();

    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <VIPProvider>
            <Navbar />
            <Routes>
                <Route path="/" element={<LandingPage />} />
                <Route path="/marketplace" element={<MarketplacePage />} />
                <Route path="/admin" element={<AdminDashboard />} />
            </Routes>
            <Footer />
        </VIPProvider>
    );
}

// --- Protected Route Component ---
function RequireAuth({ children, adminRequired = false }: { children: React.ReactElement, adminRequired?: boolean }) {
    const { user, loading, isAdmin } = useAuth();
    const location = useLocation();

    if (loading) {
        return <div className="text-center p-10">Checking authentication...</div>;
    }

    if (!user) {
        toast.error("Please log in to access this page.");
        return <Navigate to="/" state={{ from: location }} replace />;
    }

    if (adminRequired && !isAdmin) {
        toast.error("Forbidden: Admin privileges required.");
        return <Navigate to="/" replace />;
    }

    return children;
}

export default App;
