    "name": "tokenURI",
    "outputs": [{"internalType": "string", "name": "", "type": "string"}],
    "stateMutability": "view",
    "type": "function"
  }
];
interface NetworkInfo {
  chainId: number;
  name: string;
}

const getProvider = (): ethers.BrowserProvider => {
  if (!window.ethereum) {
    throw new Error('MetaMask not detected. Please install MetaMask.');
  }
  return new ethers.BrowserProvider(window.ethereum);
};

const getSigner = async (provider: ethers.BrowserProvider): Promise<ethers.Signer> => {
  await provider.send('eth_requestAccounts', []);
  return await provider.getSigner();
};

const getContract = async (readOnly = false): Promise<ethers.Contract> => {
  const provider = getProvider();
  if (readOnly) {
    return new ethers.Contract(CONTRACT_ADDRESS, EVENT_MANAGER_ABI, provider);
  } else {
    const signer = await getSigner(provider);
    return new ethers.Contract(CONTRACT_ADDRESS, EVENT_MANAGER_ABI, signer);
  }
};

export const connectWallet = async (): Promise<string | null> => {
  try {
    const provider = getProvider();
    const accounts = await provider.send('eth_requestAccounts', []);
    if (accounts && accounts.length > 0) {
      const networkCorrect = await checkNetwork();
      if (!networkCorrect.isCorrect) {
        console.warn(`Wallet connected, but on wrong network (${networkCorrect.currentNetwork?.name}). Target: ${TARGET_NETWORK_NAME}`);
