export const getAvailableTickets = async (): Promise<number[]> => {
  try {
    const contract = await getContract(true);
    const maxTickets = await contract.maxTickets();
    const ticketCount = await contract.ticketCount();
    
    // Create an array of available ticket IDs
    const availableIds: number[] = [];
    
    // Loop through all possible ticket IDs and check if they're available
    for (let i = 1; i <= ticketCount; i++) {
      // Check if the ticket has been refunded or is still available
      const isRefunded = await contract.refunded(i);
      if (isRefunded) {
        availableIds.push(i);
      }
    }
    
    return availableIds;
  } catch (error) {
    console.error("Error getting available tickets:", error);
    return [];
  }
};
