// src/pages/AdminDashboard.tsx

import React, { useState } from 'react'; // Removed unused useEffect for file storage
import { toast } from 'react-hot-toast';

// --- FIX 1: Correct the import path for API functions ---
import { uploadVipCsvApi, getAllTicketsApi, TicketData } from '../api'; // Import from '../api'

// Assuming useAuth hook is correctly exported from App or context file
import { useAuth } from '../App'; // Or '../contexts/AuthContext' etc.

import GlowingButton from '../components/GlowingButton';

// Removed StoredFileInfo and AdminDataState interfaces as that logic is simplified

const AdminDashboard: React.FC = () => {
    const { user } = useAuth();
    const [uploading, setUploading] = useState(false);
    // Keep state for the currently selected file, but don't try to persist it in sessionStorage
    const [vipFile, setVipFile] = useState<File | null>(null);
    const [fileNameDisplay, setFileNameDisplay] = useState<string>(''); // State to display selected file name

    const [allTickets, setAllTickets] = useState<TicketData[]>([]);
    const [loadingTickets, setLoadingTickets] = useState(false);

    // Removed Admin Data Button states and related useEffect/sessionStorage logic for simplicity
    // You can re-add fetching/storing general admin data if needed, separate from file handling

    const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const file = e.target.files?.[0];
        if (file) {
            // Update state with the selected file and its name
            setVipFile(file);
            setFileNameDisplay(file.name);
        } else {
            // Clear if no file selected
            setVipFile(null);
            setFileNameDisplay('');
        }
    };

    // --- FIX 2: Simplified Upload Handler ---
    const handleUploadVIPList = async () => {
        if (!vipFile) { // Just check if a file is currently selected in state
            toast.error("Please select a CSV file to upload.");
            return;
        }

        setUploading(true);
        toast.loading('Uploading VIP list...', { id: 'vip-upload' });
        try {
            // Call the correctly imported API function
            const response = await uploadVipCsvApi(vipFile);
            toast.success(`Upload successful! Processed: ${response.processedCount}, Errors: ${response.errorCount}`, {
                id: 'vip-upload',
                duration: 5000
            });

            // Clear the selected file state and displayed name after successful upload
            clearSelectedFile();

        } catch (error: any) {
            console.error("VIP Upload Failed:", error);
            toast.error(`Upload Failed: ${error.message}`, { id: 'vip-upload' });
        } finally {
            setUploading(false);
        }
    };

    const handleViewAllTickets = async () => {
        setLoadingTickets(true);
        setAllTickets([]);
        toast.loading("Fetching all minted tickets...", { id: 'fetch-tickets' });
        try {
            // Call the correctly imported API function
            const tickets = await getAllTicketsApi();
            setAllTickets(tickets);
            toast.success(`Loaded ${tickets.length} tickets.`, { id: 'fetch-tickets' });
        } catch (error: any) {
            console.error("Fetch All Tickets Failed:", error);
            toast.error(`Failed: ${error.message}`, { id: 'fetch-tickets' });
        } finally {
            setLoadingTickets(false);
        }
    };

    // Helper to clear file input state and display
    const clearSelectedFile = () => {
        setVipFile(null);
        setFileNameDisplay('');
        // Reset the actual file input element
        const input = document.getElementById('vip-file-input') as HTMLInputElement;
        if (input) input.value = '';
        toast.info('File selection cleared.');
    };


    return (
        <div className="space-y-8">
            <h1 className="text-3xl font-bold text-accent-light">Admin Dashboard</h1>
            {user && <p className='text-text-secondary mb-6'>Welcome, Admin {user.email}!</p>}

            {/* Removed Admin Session Data section for simplification - re-add if needed */}

            {/* VIP Upload Section - Simplified */}
            <section className="bg-secondary-dark p-6 rounded-lg shadow-lg border border-accent-glow/20">
                <h2 className="text-xl font-semibold text-text-main mb-4">Upload VIP List</h2>
                <div className="flex flex-col space-y-4"> {/* Increased spacing */}
                    <label htmlFor="vip-file-input" className="sr-only">Choose CSV file</label> {/* Added label for accessibility */}
                    <input
                        id="vip-file-input"
                        type="file"
                        accept=".csv"
                        onChange={handleFileChange}
                        // Basic file input styling (customize as needed)
                        className="block w-full text-sm text-text-secondary file:mr-4 file:py-2 file:px-4 file:rounded-md file:border-0 file:text-sm file:font-semibold file:bg-accent-glow file:text-white hover:file:bg-accent-light cursor-pointer"
                    />

                    {/* Display selected file name if a file is in state */}
                    {fileNameDisplay && (
                        <div className="flex items-center text-sm text-text-secondary">
                            <span>File selected: {fileNameDisplay}</span>
                            <button
                                className="ml-2 text-accent-light hover:text-accent-glow text-lg font-bold"
                                onClick={clearSelectedFile}
                                title="Clear selection"
                                aria-label="Clear file selection"
                            >
                                &times; {/* Cross symbol */}
                            </button>
                        </div>
                    )}

                    <div className="flex pt-2"> {/* Added padding top */}
                        <GlowingButton
                            onClick={handleUploadVIPList}
                            disabled={!vipFile || uploading} // Disable if no file selected or uploading
                        >
                            {uploading ? 'Uploading...' : 'Upload CSV'}
                        </GlowingButton>
                    </div>
                </div>
            </section>

            {/* Ticket Viewing Section (remains the same) */}
            <section className="bg-secondary-dark p-6 rounded-lg shadow-lg border border-accent-glow/20">
                <h2 className="text-xl font-semibold text-text-main mb-4">View All Issued Tickets</h2>
                <GlowingButton onClick={handleViewAllTickets} disabled={loadingTickets}>
                    {loadingTickets ? 'Loading...' : 'Fetch All Tickets'}
                </GlowingButton>

                {/* Ticket Table display logic remains the same */}
                {loadingTickets && <p className="text-text-secondary mt-4">Loading ticket data...</p>}
                {!loadingTickets && allTickets.length > 0 && (
                    <div className="mt-6 overflow-x-auto">
                        <table className="min-w-full divide-y divide-accent-glow/20 text-left text-sm">
                            {/* Table Head */}
                            <thead className="bg-primary-dark/50">
                                <tr>
                                    <th className="px-4 py-2 font-medium text-text-secondary">Token ID</th>
                                    <th className="px-4 py-2 font-medium text-text-secondary">Owner</th>
                                    <th className="px-4 py-2 font-medium text-text-secondary">Event</th>
                                    <th className="px-4 py-2 font-medium text-text-secondary">Seat</th>
                                    <th className="px-4 py-2 font-medium text-text-secondary">QR Hash</th>
                                </tr>
                            </thead>
                            {/* Table Body */}
                            <tbody className="divide-y divide-secondary-dark">
                                {allTickets.map((ticket) => (
                                    <tr key={ticket.tokenId} className="hover:bg-secondary-dark/50">
                                        <td className="px-4 py-2 text-text-main">{ticket.tokenId}</td>
                                        <td className="px-4 py-2 text-text-main truncate" title={ticket.owner}>
                                            {`${ticket.owner.substring(0, 6)}...${ticket.owner.substring(ticket.owner.length - 4)}`}
                                        </td>
                                        <td className="px-4 py-2 text-text-main">{ticket.eventName || 'N/A'}</td>
                                        <td className="px-4 py-2 text-text-main">{ticket.seat || 'N/A'}</td>
                                        <td className="px-4 py-2 text-text-main truncate" title={ticket.qrHash || undefined}>
                                            {ticket.qrHash || 'N/A'}
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                )}
                {!loadingTickets && allTickets.length === 0 && (
                    <p className="text-text-secondary mt-4">No ticket data loaded. Click fetch to view.</p>
                )}
            </section>
        </div>
    );
};

export default AdminDashboard;
