// src/firebase/auth.ts
import {
    signInWithPopup,
    GoogleAuthProvider,
    FacebookAuthProvider, // Note: Facebook login requires extra setup on Firebase & Facebook Developer Portal
    signOut,
    onAuthStateChanged,
    User, // Import User type
    signInWithEmailAndPassword,
    createUserWithEmailAndPassword,
    getIdToken,
    getIdTokenResult // To get custom claims
} from "firebase/auth";
import { authClient } from "./config"; // Use the configured client auth instance
import { useState, useEffect, createContext } from 'react';

const googleProvider = new GoogleAuthProvider();
const facebookProvider = new FacebookAuthProvider();

// Define the auth context type
interface AuthContextType {
    user: User | null;
    loading: boolean;
}

// Export the auth context
export const AuthContext = createContext<AuthContextType | undefined>(undefined);

// --- Sign-in Functions ---

export const signInWithGoogle = async (): Promise<User | null> => {
    try {
        const result = await signInWithPopup(authClient, googleProvider);
        return result.user;
    } catch (error: any) {
        console.error("Google Sign-in Error:", error);
        // Handle specific errors like popup closed, account exists with different credential, etc.
        throw new Error(error.message || "Google Sign-in failed.");
    }
};

export const signInWithFacebook = async (): Promise<User | null> => {
    try {
        // TODO: Ensure Facebook Login is properly configured in Firebase Console and Facebook Developer Portal
        const result = await signInWithPopup(authClient, facebookProvider);
        return result.user;
    } catch (error: any) {
        console.error("Facebook Sign-in Error:", error);
        // Handle common errors
        if (error.code === 'auth/account-exists-with-different-credential') {
             throw new Error('An account already exists with the same email address using a different sign-in method.');
        }
        throw new Error(error.message || "Facebook Sign-in failed.");
    }
};

export const signInWithEmail = async (email: string, password: string): Promise<User | null> => {
     try {
        const userCredential = await signInWithEmailAndPassword(authClient, email, password);
        return userCredential.user;
    } catch (error: any) {
        console.error("Email Sign-in Error:", error);
         // Handle specific errors like wrong password, user not found
        throw new Error(error.message || "Email Sign-in failed.");
    }
}

export const registerWithEmail = async (email: string, password: string): Promise<User | null> => {
     try {
        const userCredential = await createUserWithEmailAndPassword(authClient, email, password);
        // Optionally send verification email here: await sendEmailVerification(userCredential.user);
        return userCredential.user;
    } catch (error: any) {
        console.error("Email Registration Error:", error);
        // Handle errors like email already in use, weak password
        throw new Error(error.message || "Email Registration failed.");
    }
}

// --- Sign-out Function ---

export const signOutUser = async (): Promise<void> => {
    try {
        await signOut(authClient);
    } catch (error: any) {
        console.error("Sign Out Error:", error);
        throw new Error(error.message || "Sign Out failed.");
    }
};

// --- Auth State Listener ---

export const listenToAuthChanges = (callback: (user: User | null) => void) => {
    // Returns the unsubscribe function
    return onAuthStateChanged(authClient, callback);
};

// --- Get ID Token (for Backend Auth) ---

export const getCurrentUserIdToken = async (): Promise<string | null> => {
    try {
        const user = authClient.currentUser;
        if (!user) {
            console.log('No user is currently logged in');
            return null;
        }
        
        // Get the ID token
        const token = await user.getIdToken();
        console.log('Got Firebase token:', token.substring(0, 20) + '...'); // Log only first part for security
        return token;
    } catch (error: any) {
        console.error('Error getting Firebase token:', error);
        return null;
    }
};

// --- Check Admin Claim ---
export const checkAdminStatus = async (): Promise<boolean> => {
    if (!authClient.currentUser) {
        return false;
    }
    try {
        const idTokenResult = await getIdTokenResult(authClient.currentUser);
        return idTokenResult.claims.admin === true;
    } catch (error) {
        console.error("Error getting ID token result/claims:", error);
        return false;
    }
};

// --- Auth State Management ---
export const useAuth = () => {
    const [user, setUser] = useState<User | null>(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const unsubscribe = onAuthStateChanged(authClient, (user) => {
            setUser(user);
            setLoading(false);
        });

        return () => unsubscribe();
    }, []);

    return { user, loading };
};
