import React, { useState } from 'react';
import { useVIP } from '../contexts/VIPContext'; // Adjust path
import GlowingButton from './GlowingButton';
import { ShieldCheckIcon } from '@heroicons/react/solid';

interface VIPVerificationFormProps {
    onVerified: () => void; // Callback when verification is successful
}

const VIPVerificationForm: React.FC<VIPVerificationFormProps> = ({ onVerified }) => {
    const { checkVIPStatus, checkingVIP, isVIP, resetVIP } = useVIP();
    const [name, setName] = useState('');
    const [rollNumber, setRollNumber] = useState('');
    const [verificationAttempted, setVerificationAttempted] = useState(false);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        if (!name || !rollNumber || checkingVIP) return;

        setVerificationAttempted(true);
        const success = await checkVIPStatus(name, rollNumber);
        if (success) {
            onVerified(); // Trigger callback
        }
    };

     // If already verified, show a success message and maybe a way to reset
    if (isVIP) {
        return (
            <div className="bg-secondary-dark p-6 rounded-lg shadow-lg border border-accent-glow/30 text-center animate-fade-in">
                <ShieldCheckIcon className="h-16 w-16 text-success mx-auto mb-4"/>
                <h3 className="text-xl font-semibold text-text-main mb-2">VIP Status Verified!</h3>
                <p className="text-text-secondary mb-4">You can now access the ticket marketplace.</p>
                {/* Optional: Button to reset/re-check if needed */}
                <button onClick={resetVIP} className="text-xs text-accent-light hover:underline">Check different credentials</button>
            </div>
        );
    }

    // Show form if not verified
    return (
        <div className="bg-secondary-dark p-6 rounded-lg shadow-lg border border-accent-glow/30 animate-fade-in">
            <h3 className="text-xl font-semibold text-text-main mb-4 text-center">VIP Verification</h3>
            <p className="text-text-secondary text-sm mb-6 text-center">Enter your details to check for VIP access.</p>
            <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                    <label htmlFor="vipName" className="block text-sm font-medium text-text-secondary mb-1">Name</label>
                    <input
                        type="text"
                        id="vipName"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                        className="w-full px-3 py-2 bg-primary-dark border border-accent-glow/50 rounded-md text-text-main focus:ring-accent-light focus:border-accent-light focus:outline-none"
                        placeholder="Enter your name"
                        disabled={checkingVIP}
                    />
                </div>
                <div>
                    <label htmlFor="vipRollNumber" className="block text-sm font-medium text-text-secondary mb-1">Roll Number</label>
                    <input
                        type="text" // Or 'number' if appropriate
                        id="vipRollNumber"
                        value={rollNumber}
                        onChange={(e) => setRollNumber(e.target.value)}
                        required
                        className="w-full px-3 py-2 bg-primary-dark border border-accent-glow/50 rounded-md text-text-main focus:ring-accent-light focus:border-accent-light focus:outline-none"
                        placeholder="Enter your roll number"
                        disabled={checkingVIP}
                    />
                </div>
                <GlowingButton 
                    type="submit" 
                    className="w-full bg-accent-glow hover:bg-accent-light text-primary-dark font-medium" 
                    disabled={checkingVIP || !name || !rollNumber}
                >
                    {checkingVIP ? 'Verifying...' : 'Check VIP Status'}
                </GlowingButton>
            </form>
            {/* Only show denial message if verification was attempted and failed */}
            {verificationAttempted && isVIP === false && 
                <p className="text-center text-error mt-4">VIP Access Denied. Please check your credentials.</p>
            }
        </div>
    );
};

export default VIPVerificationForm;
